<?php if ( ! defined( 'ABSPATH' ) ) { header( 'HTTP/1.0 403 Forbidden' ); exit;}	switch  ($checkdata){//for the numpties in my life	case "":// no data is being saved set to default	$checkdata = "http://www.example.com 001 002 003 004 005 006 007 008 009 010 011 012" ;break;	default:	$checkdata = trim(preg_replace('/\s\s+/', ' ', $checkdata));//remove new lines and extra spaces	$errorstr = 'There Is An Error With The Data. Please Rectify.';// error message check without new line	if (substr($checkdata, 0, strlen($errorstr)) == $errorstr){ // Check if the error message matches the start of the string.	$checkdata = substr($checkdata, strlen($errorstr));}// If it matches, remove the error message from the beginning of the string.	$checkdata = trim($checkdata);// remove whitespace	$errorstr = 'There Is An Error With The Data. Please Rectify.' . "\n";// create error message new line removed in line 26	$check = substr_count(strtolower ($checkdata),"http");//check how many urls	if ($check >= 2) {$checkdata =  $errorstr . $checkdata ;break;}//if more tha 2 show error message	if ($check == 1) {// if only 1 url	$checkurl = explode( " ", $checkdata );// put admin data in an array   	     	$url = array_shift( $checkurl );// get the url	if (wp_http_validate_url( $url )) {break;}//everything is good 	else { $checkdata =  $errorstr . $checkdata ;break;;}}	//malformed url show error message	$checkdata = ltrim($checkdata,"-"); // remove lefthand dash	$length = strrpos($checkdata ,"-");// find last occurrence of dash	if (!$length)  {$checkdata =  $errorstr . $checkdata;break;}//if no dash show error message	$str1 = substr($checkdata,0,$length);//get first part of string	$str2 = substr($checkdata,$length +1);//get last part of string	$checkdata = $str1 . " - " . $str2;// join with a spaced -	$checkdata = trim(preg_replace('/\s\s+/', ' ', $checkdata));//remove new lines and extra spaces	}?>